SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>תחת</strong> - <strong>גערת</strong> המוכיח היא מטלת אימה בלב ה<strong>מבין</strong> יותר ממה שיחרד ה<strong>כסיל</strong> אם <strong>יוכה מאה</strong> מכות.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>תחת</strong> - ענין פחד ושבר, כמו ():</span> <strong>אל תערוץ ואל תחת</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 10 AND 1 LIMIT 1;

