UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p>ה<strong>זקנים</strong> - אף <strong>בני הבנים</strong> ישימום <strong>עטרה</strong> לראשו להתפאר בהם, אם הגונים הם; &#160; אבל ה<strong>בנים</strong>, אין דרכם כי אם <strong>להתפאר באבותם</strong>, כי יותר מחוייבים בכבוד אביהם מבכבוד אבי אביהם, ולזה עיקר התפארם באביהם.</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 6 AND 1 LIMIT 1;

