UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>תחת טובה</strong> - במקום ה<strong>טובה</strong> שעשה לו.</p>\r\n<pre><strong>לא תמוש וגו\'</strong> - כי לא ימצא מי משתדל להצילו מהרעה הואיל, ואינו משלם כמו הגמול.\r\n</pre>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>תמוש</strong> - ענין הסרה, כמו ():</span> <strong>לא ימוש</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 13 AND 1 LIMIT 1;

