UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>מצדיק וגו\'</strong> - ואף רק בדברים, כי מהראוי לגנות מעשה הרשע ולשבח מעשה הצדיק.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 15 AND 1 LIMIT 1;

