UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p>מי שכל דבריו <strong>אך למרות</strong> בה\', הוא <strong>מבקש רעה</strong> לעצמו בעולם הזה;&#160;&#160; <strong>ומלאך אכזרי ישולח</strong> (ישוסה)<strong> בו</strong> לדונו אחרי מותו.\r\n\r\n\r\n\r\n</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 11 AND 1 LIMIT 1;

