UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>מוטב ל<strong>פגוש באיש דוב שכול</strong> מבניו, אשר דרכו להרוג אשר יפגוש בעבור מרירת הלב, ועם כל זה, יותר טוב שיפגוש הוא בו משיפגוש בו איש <strong>כסיל</strong> <strong>ב</strong>עת ידבר <strong>איוולתו</strong> להסית לעבירה.\r\n\r\n\r\n\r\n</p>' WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 12 AND 1 LIMIT 1;

