UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>אהבת הרעים</strong> היא <strong>בכל עת</strong>, בין ביום טובה בין בעת צרה. אבל אהבת ה<strong>אח</strong> אינה תמידה כל כך, ורק בבוא <strong>צרה</strong> על אחיו אז ישוב להשתדל בו כי עצמו ובשרו הוא, ואם כן הרי הוא כאלו ה<strong>אח נולד</strong> <strong>ל</strong>תועלת ו<strong>צרה</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 17 AND 1 LIMIT 1;

