SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>תוקע כף</strong> - דרך <strong>חסר לב לתקוע כף</strong> <strong>בערבות</strong> ממון <strong>לפני רעהו</strong>, ר\"ל בשביל רעהו.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>ערב ערבה</strong> - מלשון ערבות ממון.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 18 AND 1 LIMIT 1;

