SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>עקש לב</strong> - המחשב עקשות.</p>\r\n<p><strong>ונהפך בלשונו</strong> - ה<strong>מהפך</strong> עצמו <strong>בלשונו</strong> לדבר תמימות ובקרבו ישים ארבו, הוא עצמו <strong>יפול בהרעה</strong> אשר חשב לזולת.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 20 AND 1 LIMIT 1;

