UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>ה<strong>כסיל</strong>, המוליך <strong>מחיר</strong> כסף למורים <strong>לקנות חכמה</strong>, <strong>למה לו זה המחיר</strong>, ומה הועיל בה? ואם לקנות חכמה לעצמו, הלא <strong>אין לו לב</strong> משיג דברים מושכלים.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 16 AND 1 LIMIT 1;

