UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>יולד כסיל - </strong>ילד דבר להיות <strong>לו לתוגה</strong> (יגון), <strong>ולא ישמח אבי נבל</strong>.</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 21 AND 1 LIMIT 1;

