UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>את פני</strong> - ה<strong>חכמה</strong> מצויה <strong>לפני המבין</strong>, כי ילמד מכל אדם.</p>\r\n<p>אבל <strong>הכסיל</strong>, ישוטטו <strong>עיניו בקצה הארץ</strong>, כי יחשוב אין מי במקומו ללמוד ממנו, כי אם מהחכמים היושבים ממרחק.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />' WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 24 AND 1 LIMIT 1;

