UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>חושך אמריו</strong> - המונע דברים בטלים הוא <strong>יודע דעת</strong>.</p>\r\n<p><strong>יקר</strong> - מי ש<strong>רוח</strong> פיו הוא <strong>יקר</strong> וחשוב, וימעט בהם, הוא <strong>איש תבונה</strong>. וכפל הדבר במ\"ש.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>חושך</strong> - מונע.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 27 AND 1 LIMIT 1;

