UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p>בן <strong>כסיל</strong> הוא <strong>מכעיס לאביו</strong>,<strong> וממרר ל</strong>אמו אשר <strong>ילדתו</strong>.</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 25 AND 1 LIMIT 1;

