UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>גם ענוש וגו\'</strong> - ר\"ל מי שהוא <strong>לא טוב</strong> ימאס כ\"כ בצדק עד אשר <strong>גם יענש לצדיק</strong> על צדקו, ויתמיד <strong>להכות נדיבים על</strong> מעשה ה<strong>יושר</strong>, כי בעיניו לרע תחשב.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 26 AND 1 LIMIT 1;

