UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>לא נאה</strong> בעיני ה<strong>נבל</strong> (אדם פחות ונבזה) דברי מעלה ו<strong>יתרון</strong>, ולא יחפוץ בהם. וכל שכן ש<strong>לא נאה שפת שקר</strong> בעיני מי ש<strong>נדבה</strong> לבו בחכמה, כי יותר ימאס החכם בשקר משימאס הנבל בדברי מעלה.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 7 AND 1 LIMIT 1;

