UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>השוחד</strong> הוא <strong>בעיני בעליו</strong> המקבלם כ<strong>אבן</strong> המסוגל להרבות <strong>חן</strong> הנושאו, כי כמו כן ישא הנותן חן בעיני המקבל, ו<strong>אל כל אשר יפנה ישכיל</strong> למצוא טעם וראיה להצדיק את הנותן.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 8 AND 1 LIMIT 1;

