UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 11 AND 1 LIMIT 1;

