UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>משיב רעה תחת </strong>(במקום) ה<strong>טובה</strong> שעשה לו, <strong>לא תמוש</strong> (תסור) <strong>רעה מביתו</strong> -&#160; כי לא ימצא מי משתדל להצילו מהרעה, הואיל ואינו משלם כמו הגמול.</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 13 AND 1 LIMIT 1;

