UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `hqblot` = '<p>אחת השאיפות העמוקות ביותר של האדם היא שתהיה לו המשכיות, לא רק המשכיות\r\n גנטית אלא גם המשכיות רעיונית, שיהיה לו למי להעביר את מחשבותיו וערכיו. \r\n</p> \r\n<p>לכן \r\n<a href=\"/tnk1/ktuv/mjly/bn_xkm.html\">בן חכם משמח את הוריו</a>, כי החכם הוא היודע ללמוד, והוא מאפשר להוריו להעביר לו את חכמתם. \r\n</p> \r\n<p>לעומת זאת, \r\n<strong>בן כסיל</strong>   מצער את הוריו, כי <a href=\"/tnk1/kma/qjrim1/ksil.html\">כסיל הוא השונא ללמוד חכמה</a>, וכשהבן כסיל, להורים אין אפשרות להעביר את חכמתם לדור הבא. כך בפסוק שלנו וגם בפסוקים נוספים: <a class=\"psuq\" href=\"/tnk1/prqim/t2810.htm#1\">משלי י1</a>: \"<q class=\"psuq\">בֵּן חָכָם יְשַׂמַּח אָב, \r\n<strong>וּבֵן כְּסִיל</strong> תּוּגַת אִמּוֹ</q>\" (<a href=\"/tnk1/ktuv/mjly/mj-10-0207.html\">פירוט</a>), <a class=\"psuq\" href=\"/tnk1/prqim/t2815.htm\">משלי טו20</a>: \"<q class=\"psuq\"><strong>בֵּן</strong> חָכָם יְשַׂמַּח אָב, \r\n<strong>וּכְסִיל</strong> אָדָם בּוֹזֶה אִמּוֹ</q>\", <a class=\"psuq\" href=\"/tnk1/prqim/t2817.htm#21\">משלי יז21</a>: \"<q class=\"psuq\"><strong>יֹלֵד כְּסִיל</strong> לְתוּגָה לוֹ וְלֹא יִשְׂמַח אֲבִי נָבָל</q>\", <a class=\"psuq\" href=\"/tnk1/prqim/t2819.htm#13\">משלי יט13</a>: \"<q class=\"psuq\">הַוֹּת לְאָבִיו \r\n<strong>בֵּן \r\nכְּסִיל</strong> וְדֶלֶף טֹרֵד מִדְיְנֵי אִשָּׁה</q>\" (<a href=\"/tnk1/ktuv/mj/19-13.html\">פירוט</a>).</p>  \r\n<p class=\"future\">אמנם, יש בן שהוא אפילו עוד יותר גרוע, <a class=\"psuq\" href=\"/tnk1/prqim/t2819.htm#26\">משלי יט26</a>: \"<q class=\"psuq\">מְשַׁדֶּד אָב, יַבְרִיחַ אֵם; בֵּן מֵבִישׁ וּמַחְפִּיר</q>\".</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 25 AND 1 LIMIT 1;

