UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>הארת עינים</strong> בדבר המסופק <strong>ישמח לב</strong>, כי אין בעולם שמחה כהתרת הספקות;&#160;&#160;&#160; ו<strong>שמועה טובה</strong>&#160; בדבר חידושי התורה <strong>תדשן</strong> (תשמין) את ה<strong>עצם</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 30 AND 1 LIMIT 1;

