UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>הפורע</strong> (משבית ומבטל) את ה<strong>מוסר</strong> הוא <strong>מואס נפשו</strong>, כי מה ראה שאינו חושש בתקנתה?&#160;&#160;&#160; אבל ה<strong>שומע תוכחת</strong>&#160;<strong>יקנה</strong> בזה <strong>לב</strong> חדש ומשכיל.</p>\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 32 AND 1 LIMIT 1;

