UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>יראת ה\'</strong> תחשב ל<strong>מוסר</strong> להאזין <strong>חכמה</strong>, ואם כן, היראה היא סיבה לחכמה וקודמת לה; &#160; וכן <strong>לפני כבוד</strong>, על כרחך היה ה<strong>ענוה</strong>, כי הענוה היא סיבה להביא את הכבוד, אם-כן היא לפניו.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 33 AND 1 LIMIT 1;

