UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<br />', `mqorot` = '<br />', `dquyot` = '  \r\n<h3>כנחש יישך</h3> \r\n<p>\r\n<strong>נשיכת נחש</strong>  מתארת את הנזק שגורמת שתיה מוגזמת, ובנוסף, \r\n<strong>נחש</strong>  הוא גם משל הממחיש את דרך הליכתו של השיכור\r\n<small class=\"small\"> (רמ\"ד וואלי)</small>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 23 AND `verse_number` = 32 AND 1 LIMIT 1;

