UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<br />', `tirgum` = '<p>- כשתהיה שיכור, \r\n<strong>עיניך</strong>   יראו הזיות \r\n<strong>זרות</strong>   ומוזרות, \r\n<strong>וליבך</strong>   יתחיל \r\n<strong>לדבר</strong>   איתך דברים מבולבלים, דבר \r\n<strong>והיפוכו</strong> -&#160;</p><p>/ - כשתהיה שיכור, \r\n<strong>עיניך</strong>   יראו נשים \r\n<strong>זרות</strong>   שיעוררו בך תאוות זנות, \r\n<strong>וליבך</strong>   \r\n<strong>ידבר</strong>   על תורות \r\n<strong>הפוכות</strong>   מתורת ה\' -</p>', `main_qod_subjects` = NULL, `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 23 AND `verse_number` = 33 AND 1 LIMIT 1;

