UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>אדם \r\n<strong>עיקש</strong> (עקום וקשה) \r\n<strong>בליבו</strong>  ודעותיו, \r\n<strong>לא</strong>  \r\n<strong>ימצא</strong> (יבחין ב)<strong>טוב</strong>  שיש באנשים וברעיונות חדשים, ולכן \r\n<strong>לא ימצא</strong> (ישיג) תוצאות \r\n<strong>טובות</strong>  בקשרים חברתיים בלימודים ובעסקים;&#160;&#160; גם הקיצוניות השניה לא טובה - אדם \r\n<strong>הפכפך</strong>, לא יציב ולא עקבי \r\n<strong>בלשונו</strong>  ודבריו, עלול \r\n<strong>ליפול</strong>  ולטעות בשגיאה \r\n<strong>רעה</strong>.</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 20 AND 1 LIMIT 1;

