UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p>מי שיוכל <strong>להחריש</strong> (להשתיק) <strong>גם</strong> את ה<strong>אויל</strong>, שלא יוסיף לדבר איוולתו, המחריש הזה ל<strong>חכם יחשב</strong>, כי דבר גדול עשה;&#160;&#160; ה<strong>אוטם</strong> (סותם) <strong>שפתי</strong> האויל (המחדיל אותו ממאמריו), יקרא <strong>נבון</strong> <small class=\"small\">(וכפל הדבר במילים שונות)</small>.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 28 AND 1 LIMIT 1;

