UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- הספר מתאים גם למתקדמים - ה<strong>חכם</strong> ש<strong>ישמע</strong> את המשלים <strong>יוסיף</strong> לעצמו <strong>לקחים</strong> ומסרים מעשיים, <strong>והנבון</strong> שיקרא בספר <strong>יקנה</strong> לעצמו <strong>תחבולות</strong> (אסטרטגיות) חדשות -</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 5 AND 1 LIMIT 1;

