UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>הדברים הנאמרים פה יועילו <strong>לתת לשוטים ערמה</strong> (התחכמות) להסיר הפתיות מלבם;&#160;&#160;&#160; <strong>לנער</strong>, למי שהוא רך בשנים, אשר יעשה מעשיו בבלי דעת ובלי מחשבה קדומה, הדברים ההם יועילו לקנות <strong>דעת ומזימה</strong> (מחשבה) - מעתה יחשוב בדבר עד לא יעשנה.</p>\r\n\r\n\r\n\r\n\r\n\r\n', `tirgum` = '<p>- שלמה החליט לפרסם את הספר כדי <strong>לתת</strong> <strong>ל</strong>אנשים ה<strong>מתפתים</strong> בקלות את ה<strong>עורמה</strong> שתציל אותם מפיתוי מסוכן, &#160; ולתת <strong>ל</strong>כל <strong>נער</strong> את ה<strong>דעת</strong> שתציל אותו מ<strong>מזימה</strong> מזיקה;</p><p>/ וכל הקורא בספר מוזמן <strong>לתת </strong>מתוכו עצות&#160;<strong>לפתיים</strong> כך שיקבלו <strong>עורמה </strong>(זהירות מהטעיות), ולעזור ל<strong>נערים</strong> ל<strong>דעת</strong> איך להיזהר מ<strong>מזימה</strong>.</p> \r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 4 AND 1 LIMIT 1;

