UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- \"את <strong>כל ההון</strong> (הרכוש)<strong> היקר </strong>שיש בעולם <strong>נמצא</strong> במסגרת המבצעים שלנו!\",&#160;&#160; \"<strong>נמלא </strong>את <strong>          בתינו </strong>בכסף ורכוש שניקח כ<strong>שלל</strong> במלחמתנו נגד התמימים!\".</p>', `mqorot` = '<p><strong>שלל</strong> הוא רכוש שלוקחים בכוח, במלחמה או בשוד.&#160; ייתכן שהשודדים אומרים לנער, שהרכוש שהם עומדים לשדוד הוא בעצמו <strong>שלל</strong>: האדם שמחזיק ברכוש עכשיו, למעשה שלל וגנב אותו מאחרים, ולכן זה מוצדק לגנוב ממנו<small class=\"small\"> (ע\"פ אור החיים)</small>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 13 AND 1 LIMIT 1;

