UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>תועלת הדברים, <strong>להודיע</strong> לבני אדם <strong>חכמה ומוסר</strong>; &#160;&#160; ואחז במשל למען <strong>הבין</strong> לזולת <strong>דברי בינה</strong>, כי הוא מבוא גדול לקנות ההבנה.</p>\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 2 AND 1 LIMIT 1;

