UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>לקחת (ללמוד) <strong>מוסר</strong> מחוכם; &#160;&#160; </p><p><strong>צדק</strong> - היא עבודת האלהים, <strong>ומשפט</strong> - מה שבין אדם לחבירו, <strong>ומשרים</strong> - בדבר המידות והנהגת האדם.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 3 AND 1 LIMIT 1;

