UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p><strong>יראת</strong>-כבוד כלפי<strong> ה\'</strong> היא הצעד <strong>הראשון</strong> ב<strong>ידיעת חכמה ומוסר</strong>!&#160;&#160; ה<strong>בזים</strong> לה\' ומנסים ללמוד חכמה בלי יראת ה\', לא יצליחו להבין את עומק החכמה ויישארו <strong>אוילים</strong> (שטחיים).</p><p>/ <strong>יראת ה\'</strong> (ראיית נוכחות ה\' המאחדת את כל המציאות) היא ה<strong>ראשית</strong> (העיקר) בלימוד \r\n  <strong>דעת חכמה ומוסר</strong>;&#160;&#160; אולם ה<strong>אוילים</strong> (השטחיים) לומדים כל דבר-חכמה בנפרד ואינם רואים את האחדות ולכן <strong>בזים</strong> לה\'.</p>' WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 7 AND 1 LIMIT 1;

