UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>אל תלך</strong> <strong>בדרך</strong> אשר <strong>אתם</strong>, אשר הם הולכים בה, שלא תלמוד ממעשיהם;&#160;&#160; <strong>מנע רגלך מנתיבתם</strong> - מהדרך שהם הולכים בה -</p>\r\n\r\n\r\n\r\n\r\n\r\n', `tirgum` = '<p>\r\n- <strong>בני! אל תלך בדרך </strong>עם חטאים (אל תשתף עמם פעולה), &#160;&#160; <strong>מנע</strong> (הרחק) את<strong> רגלך</strong> <strong>מהנתיב</strong> שלהם (אל תחקה אותם) -</p>' WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 15 AND 1 LIMIT 1;

