UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '   \r\n<p>- <strong>כן</strong> לאותה תוצאה מובילות <strong>האורחות</strong> (הדרכים) של <strong>כל בוצע בצע</strong>, הרוצה להשיג רווח קל ומהיר:&#160;&#160;&#160; הרצון הזה יגרום לו ליפול בפח ו<strong>יקח</strong> לו <strong>את הנפש</strong> (יהרוג אותו)!</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 19 AND 1 LIMIT 1;

