UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p> כאשר <strong>תשכב</strong> לישון - <strong>לא</strong> תהיה טרוד ב<strong>פחדים</strong>;&#160;&#160;&#160; מייד כש<strong>תשכב</strong> - תירדם ותישן <strong>שֵנָה</strong>  <strong>עֲרֵבָה</strong> (מתוקה ונעימה);</p>\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 24 AND 1 LIMIT 1;

