UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `mqorot` = '<h3>איך שנאתי מוסר?!</h3><p>\"<q class=\"mfrj\">יש להקשות: מה מקום אל התימה הזה? כי כבר ידוע שיצר לב\r\n האדם רע מנעוריו, והוא שמתעה אותו ומכשילו בכמה עבירות! &#160; ואמנם התירוץ \r\nלזה, כי יש באדם שני יצרים: יצר הטוב שנקרא <strong>לב</strong>, ויצר הרע שנקרא לבב.\r\n ואין לתמוה על היצר הרע שיהיה להוט אחרי העבירות, כי הוא לפי טבעו, אבל \r\nהתימה הגדול על היצר הטוב, איך אפשר שהסכים עם היצר הרע לשנוא את המוסר \r\nולנאוץ את התוכחות... וזהו שאמר: ותוכחת נאץ <strong>ליבי</strong>... כי הטוב היה צריך להתגבר על הרע</q>\"<small class=\"small\"> (רמ\"ד ואלי)</small>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 5 AND `verse_number` = 12 AND 1 LIMIT 1;

