UPDATE `sgulot` SET `prefix` = NULL, `mqorot` = '<p><strong>חיק</strong><a href=\"http://localhost/tnk1/kma/qjrim1/xeq.html\"> = האויר הסמוך לחזה</a>.\r\n זהו המקום שבו מחבקים, כמו ב<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t0116.htm#5\">בראשית טז5</a>: \"<q class=\"psuq\">אָנֹכִי נָתַתִּי שִׁפְחָתִי \r\n<strong>בְּחֵיקֶךָ</strong></q>\", וגם המקום שבו מסתירים דברים, כמו&#160; ב<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t2817.htm#23\">משלי יז23</a>: \"<q class=\"psuq\">שֹׁחַד \r\n<strong>מֵחֵיק</strong> רָשָׁע יִקָּח</q>\" (<a href=\"http://localhost/tnk1/ktuv/mjly/mj-17-23.html\">פירוט</a>). הפסוק שלנו משתמש \r\nבמילה בכפל משמעות - הנער רוצה לחבק אשה זרה ולהסתיר את זה, אבל המורה \r\nמסביר לו שזה לא יילך, כי ה\' רואה הכל -</p>', `dquyot` = '<p><strong>חיק</strong><a href=\"http://localhost/tnk1/kma/qjrim1/xeq.html\"> = האויר הסמוך לחזה</a>.\r\n זהו המקום שבו מחבקים, כמו ב<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t0116.htm#5\">בראשית טז5</a>: \"<q class=\"psuq\">אָנֹכִי נָתַתִּי שִׁפְחָתִי \r\n<strong>בְּחֵיקֶךָ</strong></q>\", וגם המקום שבו מסתירים דברים, כמו&#160; ב<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t2817.htm#23\">משלי יז23</a>: \"<q class=\"psuq\">שֹׁחַד \r\n<strong>מֵחֵיק</strong> רָשָׁע יִקָּח</q>\" (<a href=\"http://localhost/tnk1/ktuv/mjly/mj-17-23.html\">פירוט</a>). הפסוק שלנו משתמש \r\nבמילה בכפל משמעות - הנער רוצה לחבק אשה זרה ולהסתיר את זה, אבל המורה \r\nמסביר לו שזה לא יילך, כי ה\' רואה הכל -</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 5 AND `verse_number` = 20 AND 1 LIMIT 1;

