UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p><strong>עוונותיו</strong> של האדם <strong>לוכדים</strong> אותו לחברה רעה, יחד <strong>עם הרשע</strong>; </p><p>הוא קשור לרשעים <strong>בחבלים</strong> של <strong>חטא</strong>, המספקים לו <strong>תמיכה</strong> חברתית, ואינו יכול להתנתק -</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 5 AND `verse_number` = 22 AND 1 LIMIT 1;

