UPDATE `sgulot` SET `tirgum` = '<p>יש בעולם <strong>אדם בליעל</strong>, שאינו מועיל אלא רק מזיק; <strong>איש אוון</strong>, שבדבריו אין אמת אלא רק שקר. אפשר לזהות אותו לפי שפת הגוף -</p><p> <strong>הפה</strong> שלו <strong>הולך בעיקשות</strong>, מתעקם ומתעוות כשהוא משקר;</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 6 AND `verse_number` = 12 AND 1 LIMIT 1;

