UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `mqorot` = '<h3>מה זה <strong>יפיח</strong>?</h3><p>1. רוב המפרשים פירשו מהשורש <strong>פוח</strong> - יוציא רוח מפיו, ידבר.<br /><br />2. אולם \"דעת מקרא\" על ספר משלי פירשו מהשורש <strong>יפח</strong> - שבאוגריתית משמעו עֵד.</p>', `dquyot` = '<h3>מה זה <strong>יפיח</strong>?</h3><p>1. רוב המפרשים פירשו מהשורש <strong>פוח</strong> - יוציא רוח מפיו, ידבר.<br /><br />2. אולם \"דעת מקרא\" על ספר משלי פירשו מהשורש <strong>יפח</strong> - שבאוגריתית משמעו עֵד.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 6 AND `verse_number` = 19 AND 1 LIMIT 1;

