UPDATE `sgulot` SET `tirgum` = '<p><strong>בני! נצור, </strong>שמור היטב וטפח את ה<strong>מצוות </strong>שקיבלת במסורת מ<strong>אביך, ואל תטוש</strong> (תעזוב את)ה<strong>תורה </strong>שקיבלת במסורת מ<strong>אמך!</strong></p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 6 AND `verse_number` = 20 AND 1 LIMIT 1;

