UPDATE `sgulot` SET `prefix` = NULL, `mqorot` = '<h3>מדוע נזכרו שני משלים בפסוקים 27-28?</h3><p>1. המשל בפסוק 27 הוא כנגד האזהרה בפסוק 25: \"<q class=\"psuq\">אל תחמוד יופייה בלבבך</q>\". &#160; <strong>חיק</strong><a href=\"http://localhost/tnk1/kma/qjrim1/xeq.html\"> = האויר הסמוך ללב</a>;&#160; הכנסת גחלי-אש אל החיק היא משל להכנסת הרהורי-עבירה אל הלב;&#160;&#160;&#160; המשל בפסוק 28 הוא כנגד האזהרה בפסוק 29: \"<q class=\"psuq\">כן הבא אל אשת רעהו</q>\".&#160;&#160;&#160; הרגליים ההולכות על גחלים הם משל להליכה אל אשה נואפת.</p><p>2. וייתכן שהמשל בפסוק 27 הוא כנגד סוף פסוק 29: \"<q class=\"psuq\">לא יינקה כל הנוגע בה</q>\". <strong>חיק</strong><a href=\"http://localhost/tnk1/kma/qjrim1/xeq.html\"> = המקום שבו מחבקים</a> (ובפרט: המקום שבו איש מחבק את אשת-חיקו ואשה מחבקת את איש-חיקה);&#160; הכנסת גחלי-אש אל החיק היא משל לחיבוק אשה זרה. המשל בפסוק 28 הוא כנגד תחילת פסוק 29: \"<q class=\"psuq\">כן הבא אל אשת רעהו</q>\", ולפי זה הפסוקים 27-29 מסודרים במבנה כיאסטי:</p>', `ecot` = '<p>נראה לך שתוכל לחשוב על אשה זרה בלי להתפתות?! זו אשליה:</p><p>אם אדם ינסה להרים גחלים בוערות בחיקו, ליד ליבו, מתחת לבגדיו, האש לא \r\nתישאר שם - היא תבער במהירות ותשרוף את כל בגדיו. גם המחשבות על יופיה של \r\nאשה הן מחשבות לוהטות ושורפות: הן לא יישארו בתוך הלב אלא יבעירו את כל \r\nהגוף כולל הבגדים, ויגרמו לו לעבור עבירה.</p><p>כך גם לגבי כל מעשה רע שהאדם מתאווה לעשותו, למשל אכילת מאכלים משמינים לאדם שצריך לעשות דיאטה - ככל שהאדם חושב עליו\r\n יותר, כך המחשבות מלהיטות ומבעירות את גופו, וגורמות לו לחטוא.</p>', `dquyot` = '<h3>מדוע נזכרו שני משלים בפסוקים 27-28?</h3><p>1. המשל בפסוק 27 הוא כנגד האזהרה בפסוק 25: \"<q class=\"psuq\">אל תחמוד יופייה בלבבך</q>\". &#160; <strong>חיק</strong><a href=\"/tnk1/kma/qjrim1/xeq.html\"> = האויר הסמוך ללב</a>;&#160; הכנסת גחלי-אש אל החיק היא משל להכנסת הרהורי-עבירה אל הלב;&#160;&#160;&#160; המשל בפסוק 28 הוא כנגד האזהרה בפסוק 29: \"<q class=\"psuq\">כן הבא אל אשת רעהו</q>\".&#160;&#160;&#160; הרגליים ההולכות על גחלים הם משל להליכה אל אשה נואפת.</p><p>2. וייתכן שהמשל בפסוק 27 הוא כנגד סוף פסוק 29: \"<q class=\"psuq\">לא יינקה כל הנוגע בה</q>\". <strong>חיק</strong><a href=\"http://localhost/tnk1/kma/qjrim1/xeq.html\"> = המקום שבו מחבקים</a>\r\n (ובפרט: המקום שבו איש מחבק את אשת-חיקו ואשה מחבקת את איש-חיקה);&#160; הכנסת \r\nגחלי-אש אל החיק היא משל לחיבוק אשה זרה. המשל בפסוק 28 הוא כנגד תחילת \r\nפסוק 29: \"<q class=\"psuq\">כן הבא אל אשת רעהו</q>\", ולפי זה הפסוקים 27-29 מסודרים במבנה כיאסטי:</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 6 AND `verse_number` = 27 AND 1 LIMIT 1;

