UPDATE `sgulot` SET `tirgum` = '<p><strong>בנים!</strong> <strong>שמעו</strong> את דברי ה<strong>מוסר</strong> העוברים <strong>מאב</strong> לבן, <strong>והקשיבו </strong>כדי <strong>שתדעו ותבינו </strong>את ההגיון מאחריהם -</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 4 AND `verse_number` = 1 AND 1 LIMIT 1;

