UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- <strong>כי</strong> אני <strong>נותן</strong> לכם את העצות <strong>הטובות</strong> ביותר <strong>שלקחתי</strong> וקיבלתי מהוריי; את <strong>התורה</strong> הזאת כדאי לכם לשמור <strong>ולא לעזוב</strong>.</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 4 AND `verse_number` = 2 AND 1 LIMIT 1;

