UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- והמצווה העיקרית שלי אליך היא: <strong>קנה חכמה, קנה בינה</strong> כל הזמן ובכל מחיר! <strong>אל תשכח</strong> בכל עת להמשיך ללמוד, להחכים ולהתבונן! <strong>אל תטה</strong> אף פעם מהמסלול הזה!</p><p>/ וגם כאשר <strong>תקנה</strong> <strong>חכמה ובינה</strong> ממקורות אחרים, <strong>אל תשכח</strong> את הערכים שלמדת בבית, <strong>ואל תטה</strong> מהעקרונות <strong>שאמרתי</strong> ולימדתי אותך -</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 4 AND `verse_number` = 5 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- אף פעם <strong>אל תעזוב</strong> את דרך החכמה והמצוה, והיא <strong>תשמור</strong> עליך מצרות;&#160;&#160;&#160;&#160; <strong>אהוב</strong> את החכמה ואת המצוה, למד וקיים מצוות בשמחה וברצון, והיא <strong>תנצור</strong> (תשגיח ותטפח) אותך.</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 4 AND `verse_number` = 6 AND 1 LIMIT 1;

