UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>ב<strong>ראשית</strong> דרכך בעולם <strong>החכמה</strong> - <strong>קנה</strong> (למד) <strong>חכמה</strong> מאחרים;&#160;&#160;&#160; ובהמשך דרכך - השתמש <strong>בכל</strong> הידע <strong>שקנית</strong> <strong>וקנה </strong>באמצעותו<strong> בינה</strong> והסק מסקנות חדשות משלך;</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 4 AND `verse_number` = 7 AND 1 LIMIT 1;

