UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p><strong>סלסל</strong> (התעסק וטַפֵּח) את חכמתך, והיא <strong>תרומם</strong> אותך (תביא אותך למעמד גבוה); &#160; <strong>חבק</strong> את החכמה באהבה, והיא תביא לך <strong>כבוד</strong> -</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 4 AND `verse_number` = 8 AND 1 LIMIT 1;

