UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `hqblot` = '<p><strong>יפיח כזבים</strong> ו<strong>עד שקר</strong> נזכרים בפסוקים נוספים בספר משלי, <a class=\"psuq\" href=\"/tnk1/prqim/t2812.htm#17\">משלי יב17</a>: \"<q class=\"psuq\">יָפִיחַ אֱמוּנָה יַגִּיד צֶדֶק, <strong>וְעֵד שְׁקָרִים</strong> מִרְמָה</q>\" (<a href=\"/tnk1/ktuv/mj/12-17.html\">פירוט</a>), <a class=\"psuq\" href=\"/tnk1/prqim/t2814.htm#5\">משלי יד5</a>: \"<q class=\"psuq\">עֵד אֱמוּנִים לֹא יְכַזֵּב, <strong>וְיָפִיחַ כְּזָבִים</strong> <strong>עֵד שָׁקֶר</strong></q>\" (<a href=\"/tnk1/ktuv/mj/14-05.html\">פירוט</a>), <a class=\"psuq\" href=\"/tnk1/prqim/t2819.htm#5\">משלי יט5</a>: \"<q class=\"psuq\"><strong>עֵד שְׁקָרִים</strong> לֹא יִנָּקֶה, <strong>וְיָפִיחַ כְּזָבִים</strong> לֹא יִמָּלֵט</q>\" (<a href=\"/tnk1/ktuv/mj/19-05.html\">פירוט</a>), <a class=\"psuq\" href=\"/tnk1/prqim/t2819.htm#9\">משלי יט9</a>: \"<q class=\"psuq\"><strong>עֵד שְׁקָרִים</strong> לֹא יִנָּקֶה, <strong>וְיָפִיחַ כְּזָבִים</strong> יֹאבֵד</q>\" (<a href=\"/tnk1/ktuv/mj/19-05.html\">פירוט</a>), <a class=\"psuq\" href=\"/tnk1/prqim/t2825.htm#18\">משלי כה18</a>: \"<q class=\"psuq\">מֵפִיץ וְחֶרֶב וְחֵץ שָׁנוּן - אִישׁ עֹנֶה בְרֵעֵהוּ <strong>עֵד שָׁקֶר</strong></q>\" (<a href=\"/tnk1/ktuv/mj/25-18.html\">פירוט</a>).</p><p>והשורש לכולם בתורה, <a class=\"psuq\" href=\"/tnk1/prqim/t0220.htm#12\">שמות כ12</a>: \"<q class=\"psuq\">לֹא תַעֲנֶה בְרֵעֲךָ <strong>עֵד שָׁקֶר</strong></q>\", <a class=\"psuq\" href=\"/tnk1/prqim/t0519.htm#18\">דברים יט18</a>: \"<q class=\"psuq\">וְדָרְשׁוּ הַשֹּׁפְטִים הֵיטֵב וְהִנֵּה <strong>עֵד שֶׁקֶר</strong> <strong>הָעֵד, שֶׁקֶר</strong> עָנָה בְאָחִיו</q>\"</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 6 AND `verse_number` = 19 AND 1 LIMIT 1;

