UPDATE `sgulot` SET `prefix` = NULL, `mqorot` = '<p>מדוע נזכרו שני משלים בפסוקים 27-28?</p><p>1. המשל בפסוק 27 הוא כנגד האזהרה בפסוק 25: \"<q class=\"psuq\">אל תחמוד יופייה בלבבך</q>\". &#160; <strong>חיק</strong><a href=\"http://localhost/tnk1/kma/qjrim1/xeq.html\"> = האויר הסמוך ללב</a>;&#160; הכנסת גחלי-אש אל החיק היא משל להכנסת הרהורי-עבירה אל הלב;&#160;&#160;&#160; המשל בפסוק 28 הוא כנגד האזהרה בפסוק 29: \"<q class=\"psuq\">כן הבא אל אשת רעהו</q>\".&#160;&#160;&#160; הרגליים ההולכות על גחלים הם משל להליכה אל אשה נואפת.</p><p>2. וייתכן שהמשל בפסוק 27 הוא כנגד סוף פסוק 29: \"<q class=\"psuq\">לא יינקה כל הנוגע בה</q>\". <strong>חיק</strong><a href=\"http://localhost/tnk1/kma/qjrim1/xeq.html\"> = המקום שבו מחבקים</a>\r\n (ובפרט: המקום שבו איש מחבק את אשת-חיקו ואשה מחבקת את איש-חיקה);&#160; הכנסת \r\nגחלי-אש אל החיק היא משל לחיבוק אשה זרה. המשל בפסוק 28 הוא כנגד תחילת \r\nפסוק 29: \"<q class=\"psuq\">כן הבא אל אשת רעהו</q>\", ולפי זה הפסוקים 27-29 מסודרים במבנה כיאסטי:</p>', `dquyot` = '<h3>מדוע נזכרו שני משלים בפסוקים 27-28?</h3><p>1. המשל בפסוק 27 הוא כנגד האזהרה בפסוק 25: \"<q class=\"psuq\">אל תחמוד יופייה בלבבך</q>\". &#160; <strong>חיק</strong><a href=\"http://localhost/tnk1/kma/qjrim1/xeq.html\"> = האויר הסמוך ללב</a>;&#160; הכנסת גחלי-אש אל החיק היא משל להכנסת הרהורי-עבירה אל הלב;&#160;&#160;&#160; המשל בפסוק 28 הוא כנגד האזהרה בפסוק 29: \"<q class=\"psuq\">כן הבא אל אשת רעהו</q>\".&#160;&#160;&#160; הרגליים ההולכות על גחלים הם משל להליכה אל אשה נואפת.</p><p>2. וייתכן שהמשל בפסוק 27 הוא כנגד סוף פסוק 29: \"<q class=\"psuq\">לא יינקה כל הנוגע בה</q>\". <strong>חיק</strong><a href=\"http://localhost/tnk1/kma/qjrim1/xeq.html\"> = המקום שבו מחבקים</a>\r\n (ובפרט: המקום שבו איש מחבק את אשת-חיקו ואשה מחבקת את איש-חיקה);&#160; הכנסת \r\nגחלי-אש אל החיק היא משל לחיבוק אשה זרה. המשל בפסוק 28 הוא כנגד תחילת \r\nפסוק 29: \"<q class=\"psuq\">כן הבא אל אשת רעהו</q>\", ולפי זה הפסוקים 27-29 מסודרים במבנה כיאסטי:</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 6 AND `verse_number` = 28 AND 1 LIMIT 1;

